mapper.update:
UPDATE review
SET read_count = read_count + 1
WHERE bno = #{bno}

mapper.select:
SELECT read_count
FROM review
WHERE bno = #{bno}

mapper.update:
UPDATE review
SET read_count = read_count - 1
WHERE bno = #{bno}

UPDATE review
SET title = #{title},
content = #{content}
WHERE bno = #{bno}

mapper.select:
SELECT bno, title, content, writer, regDate
FROM review
WHERE bno = #{bno}
